mapper.select:

select 

,

from nk_script_def_h
where SCRIPT_NAME = #{scriptName,jdbcType=VARCHAR}
  and VERSION = #{version,jdbcType=VARCHAR}

mapper.delete:

delete from nk_script_def_h
where SCRIPT_NAME = #{scriptName,jdbcType=VARCHAR}
  and VERSION = #{version,jdbcType=VARCHAR}

mapper.update:

update nk_script_def_h
set SCRIPT_TYPE = #{scriptType,jdbcType=VARCHAR},
  SCRIPT_DESC = #{scriptDesc,jdbcType=VARCHAR},
  SCRIPT_DOC = #{scriptDoc,jdbcType=VARCHAR},
  STATE = #{state,jdbcType=VARCHAR},
  OWNER = #{owner,jdbcType=VARCHAR},
  CREATED_TIME = #{createdTime,jdbcType=BIGINT},
  UPDATED_TIME = #{updatedTime,jdbcType=BIGINT}
where SCRIPT_NAME = #{scriptName,jdbcType=VARCHAR}
  and VERSION = #{version,jdbcType=VARCHAR}
